components/dh-custom/common/social-links:
{{#if social_media}}
    <ul class="socialLinks-custom">
        {{#each social_media}}
            <li class="socialLinks-item-custom">
                <a class="icon-custom icon--{{display_name}}" href="{{url}}" target="_blank" rel="noopener">
                    <svg><use xlink:href="#icon-{{display_name}}" /></svg>
                </a>
            </li>
        {{/each}}
    </ul>
{{/if}}

components/products/bulk-discount-rates:
{{#if bulk_discount_rates.length}}
    {{#if product}}<dt class="productView-info-name">{{lang 'products.bulk_pricing.title'}}</dt>{{/if}}
    <dd class="productView-info-value">
        <a href="{{product.url}}#bulk_pricing"
           {{#unless is_ajax }}data-reveal-id="modal-bulk-pricing"{{/unless}}>
            {{lang 'products.bulk_pricing.view'}}
        </a>
    </dd>
    <div id="modal-bulk-pricing" class="modal modal--small" data-reveal>
        <div class="modal-header">
            <h2 class="modal-header-title">{{lang 'products.bulk_pricing.modal_title'}}</h2>
            {{> components/common/modal/modal-close-btn}}
        </div>
        <div class="modal-body">
            <p>{{lang 'products.bulk_pricing.instructions'}}</p>
            <ul>
                {{#each bulk_discount_rates}}
                <li>
                    {{lang 'products.bulk_pricing.range' min=min max=max}}
                    {{#if type '===' 'percent'}}
                    {{lang 'products.bulk_pricing.percent' discount=discount.formatted}}
                    {{else if type '===' 'fixed'}}
                    {{lang 'products.bulk_pricing.fixed' discount=discount.formatted}}
                    {{else if type '===' 'price'}}
                    {{lang 'products.bulk_pricing.price' discount=discount.formatted}}
                    {{/if}}
                </li>
                {{/each}}
            </ul>
        </div>
    </div>
{{/if}}

components/products/card:
{{inject "isProductCardPresented" true}}

<article class="card {{#or price.sale_price_with_tax.value price.sale_price_without_tax.value}}show-badges{{/or}}
    {{#if alternate}} card--alternate1{{/if}}" data-test="card-{{id}}" {{#if settings.data_tag_enabled}}
    data-event-type="{{event}}" data-entity-id="{{id}}" data-position="{{position}}" data-name="{{name}}"
    data-product-category="
        {{#each category}}
            {{#if @last}}
                {{this}}
            {{else}}
                {{this}},
            {{/if}}
        {{/each}}" data-product-brand="{{brand.name}}" data-product-price="
        {{#if price.with_tax}}
            {{price.with_tax.value}}
        {{else}}
            {{price.without_tax.value}}
        {{/if}}" {{/if}}>
    <figure class="card-figure">
        {{#if stock_level '===' 0}}
        {{> components/products/product-badge
        badge-type='sold-out'
        badge_view=theme_settings.product_sold_out_badges
        badge_label=theme_settings.pdp_sold_out_label
        page_builder_key="pdp_sold_out_label"
        default_translation=(lang "page_builder.pdp_sold_out_label")
        }}
        {{else}}
        {{#or price.sale_price_with_tax.value price.sale_price_without_tax.value}}
        {{> components/products/product-badge
        badge-type='sale'
        badge_view=theme_settings.product_sale_badges
        badge_label=theme_settings.pdp_sale_badge_label
        page_builder_key="pdp_sale_badge_label"
        default_translation=(lang "page_builder.pdp_sale_badge_label")
        }}
        {{/or}}
        {{/if}}
        <a href="{{url}}" class="card-figure__link" aria-label="{{name}},{{> components/products/product-aria-label}}"
            {{#if settings.data_tag_enabled}} data-event-type="product-click" {{/if}}>
            <div class="card-img-container">
                {{> components/common/responsive-img
                image=image
                class="card-image"
                fallback_size=theme_settings.productgallery_size
                lazyload=theme_settings.lazyload_mode
                default_image=theme_settings.default_image_product
                }}
            </div>
        </a>
        {{#if settings.show_wishlist}}
        <div class="card-wishlist">
            <a class="needsclick wishlistbtn" rel="nofollow" href="javascript:void(0)" {{#if
                customer}}data-add-href="/wishlist.php?action=add&product_id={{id}}" data-href="{{add_to_wishlist_url}}"
                {{else}}data-href="/login.php" {{/if}}>
                <i class="icon out">
                    <svg>
                        <use xlink:href="#icon-wishlist-heart"></use>
                    </svg>
                </i>
                <i class="icon hover">
                    <svg>
                        <use xlink:href="#icon-wishlist-heart-over"></use>
                    </svg>
                </i>
            </a>
        </div>
        {{/if}}

        <figcaption class="card-figcaption">
            <div class="card-figcaption-body">
                {{#unless hide_product_quick_view}}
                {{#if theme_settings.show_product_quick_view}}
                {{#if settings.data_tag_enabled}}
                <button type="button" class="button button--small card-figcaption-button quickview"
                    data-event-type="product-click" data-product-id="{{id}}">{{lang 'products.quick_view'}}</button>
                {{else}}
                <button type="button" class="button button--small card-figcaption-button quickview"
                    data-product-id="{{id}}">{{lang 'products.quick_view'}}</button>
                {{/if}}
                {{/if}}
                {{/unless}}
                {{#or customer (if theme_settings.restrict_to_login '!==' true)}}
                {{#if show_cart_action}}
                {{#if has_options}}
                <a href="{{url}}" data-event-type="product-click"
                    class="button button--small card-figcaption-button card-second-quickview-trigger"
                    data-product-id="{{id}}">{{lang 'products.add_to_cart'}}</a>
                {{/if}}
                {{#if pre_order}}
                <a href="{{pre_order_add_to_cart_url}}" data-event-type="product-click"
                    class="button button--small card-figcaption-button">{{lang 'products.pre_order'}}</a>
                {{/if}}
                {{#if add_to_cart_url }} <!-- {{add_to_cart_url}} -->
                <a href="javascript:void(0)" rel="nofollow" data-product-id="{{id}}" data-event-type="product-click"
                    data-button-type="add-cart"
                    class="button button--small card-figcaption-button card-addtocart-button">
                    {{lang 'products.add_to_cart'}}
                    <span class="loading-indicator"><svg xmlns="http://www.w3.org/2000/svg"
                            style="margin:auto;background:0 0" width="200" height="200" viewBox="0 0 100 100"
                            preserveAspectRatio="xMidYMid" display="block">
                            <circle cx="50" cy="50" fill="none" stroke="#fff" stroke-width="10" r="35"
                                stroke-dasharray="164.93361431346415 56.97787143782138">
                                <animateTransform attributeName="transform" type="rotate" repeatCount="indefinite"
                                    dur="1s" values="0 50 50;360 50 50" keyTimes="0;1" />
                            </circle>
                        </svg></span>
                </a>
                <span class="product-status-message aria-description--hidden">{{lang
                    'category.add_cart_announcement'}}</span>
                {{/if}}
                {{#if out_of_stock_message }}
                <a href="{{url}}" data-event-type="product-click" class="button button--small card-figcaption-button"
                    data-product-id="{{id}}">{{out_of_stock_message}}</a>
                {{/if}}
                {{/if}}
                {{/or}}
            </div>
        </figcaption>
    </figure>
    <div class="card-body">
        {{#and rating show_rating}}
        <p class="card-text" data-test-info-type="productRating">
            <span class="rating--small">
                {{> components/products/ratings rating_target=name rating=rating}}
            </span>
        </p>
        {{/and}}
        <div class="stamped-product-wrap">
            <span class="stamped-product-reviews-badge xydemeo" data-id="{{id}}" data-product-sku="{{sku}}"
                style="display:block;"></span>
        </div>
        {{#if brand.name}}
        <p class="card-text brand-name" data-test-info-type="brandName">{{brand.name}}</p>
        {{/if}}
        <h3 class="card-title">
            <a aria-label="{{name}},{{> components/products/product-aria-label}}" href="{{url}}" {{#if
                settings.data_tag_enabled}}data-event-type="product-click" {{/if}}>
                {{name}}
            </a>
        </h3>

        <div class="card-text card-price-block" data-test-info-type="price">
            {{#or customer (if theme_settings.restrict_to_login '!==' true)}}
            {{> components/products/price price=price}}
            {{else}}
            {{> components/common/login-for-pricing}}
            {{/or}}
        </div>
        {{> components/products/bulk-discount-rates}}

        {{#if show_compare}}
        <label class="button1 compare-btn button--small card-figcaption-button" for="compare-{{id}}">
            <input type="checkbox" class="styled-checkbox" name="products[]" value="{{id}}" id="compare-{{id}}"
                data-compare-id="{{id}}">
            <span for="compare-{{id}}">{{lang 'products.compare'}}</span>
        </label>
        {{/if}}


    </div>
</article>

components/products/price:
{{!-- There are 2 code paths for generating the PDP pricing HTML determined by the following #and condition.
If a 'price_range' exists then we generate all HTML in price-range.html, otherwise it is defined here. Both code
paths generate the same HTML structure with some differences in whether that element is displayed by default (css styling).

If you are making a change here or in price-range.html, you probably want to make that change in both files. --}}

{{#and price.price_range (if theme_settings.price_ranges '==' true)}}
    {{> components/products/price-range price=price schema_org=schema_org}}
{{else}}
    {{#if price.with_tax}}
        <div class="price-section price-section--withTax rrp-price--withTax" {{#unless price.rrp_with_tax}}style="display: none;"{{/unless}}>
            <span data-page-builder-key="pdp-retail-price-label" data-default-translation="{{lang "page_builder.pdp-retail-price-label"}}">
                {{theme_settings.pdp-retail-price-label}}
            </span>
            <span data-product-rrp-with-tax class="price price--rrp">
                {{price.rrp_with_tax.formatted}}
            </span>
        </div>
        <div class="price-section price-section--withTax non-sale-price--withTax" {{#unless price.non_sale_price_with_tax}}style="display: none;"{{/unless}}>
            <span data-page-builder-key="pdp-non-sale-price-label" data-default-translation="{{lang "page_builder.pdp-non-sale-price-label"}}">
                {{theme_settings.pdp-non-sale-price-label}}
            </span>
            <span data-product-non-sale-price-with-tax class="price price--non-sale">
                {{price.non_sale_price_with_tax.formatted}}
            </span>
        </div>
        <div class="price-section price-section--withTax" {{#if schema_org}}itemprop="offers" itemscope itemtype="https://schema.org/Offer"{{/if}}>
            <span class="price-label" {{#if price.non_sale_price_with_tax}}style="display: none;"{{/if}}>
                {{theme_settings.pdp-price-label}}
            </span>
            <span class="price-now-label"
                  {{#unless price.non_sale_price_with_tax}}style="display: none;"{{/unless}}
                  data-page-builder-key="pdp-sale-price-label"
                  data-default-translation="{{lang "page_builder.pdp-sale-price-label"}}"
            >
                {{theme_settings.pdp-sale-price-label}}
            </span>
            <span data-product-price-with-tax class="price price--withTax">{{price.with_tax.formatted}}</span>
            {{#if schema_org}}
                <meta itemprop="availability" itemtype="https://schema.org/ItemAvailability"
                	content="https://schema.org/{{#if product.pre_order}}PreOrder{{else if product.out_of_stock}}OutOfStock{{else if product.can_purchase '===' false}}OutOfStock{{else}}InStock{{/if}}">
                <meta itemprop="itemCondition" itemtype="https://schema.org/OfferItemCondition" content="https://schema.org/{{product.condition}}Condition">
                <meta itemprop="priceCurrency" content="{{currency_selector.active_currency_code}}">
                <meta itemprop="url" content="{{product.url}}">
                <div itemprop="priceSpecification" itemscope itemtype="https://schema.org/PriceSpecification">
                    <meta itemprop="price" content="{{price.with_tax.value}}">
                    <meta itemprop="priceCurrency" content="{{currency_selector.active_currency_code}}">
                    <meta itemprop="valueAddedTaxIncluded" content="true">
                </div>
            {{/if}}
            {{#if price.without_tax}}
                <abbr title="{{lang 'products.including_tax'}}">{{lang 'products.price_with_tax' tax_label=price.tax_label}}</abbr>
            {{/if}}
        </div>
    {{/if}}
    {{#if price.without_tax}}
        <div class="price-section price-section--withoutTax rrp-price--withoutTax{{#if price.with_tax}} price-section--minor{{/if}}" {{#unless price.rrp_without_tax}}style="display: none;"{{/unless}}>
            <span data-page-builder-key="pdp-retail-price-label" data-default-translation="{{lang "page_builder.pdp-retail-price-label"}}">
                {{theme_settings.pdp-retail-price-label}}
            </span>
            <span data-product-rrp-price-without-tax class="price price--rrp">
                {{price.rrp_without_tax.formatted}}
            </span>
        </div>
        <div class="price-section price-section--withoutTax non-sale-price--withoutTax{{#if price.with_tax}} price-section--minor{{/if}}" {{#unless price.non_sale_price_without_tax}}style="display: none;"{{/unless}}>
            <span data-page-builder-key="pdp-non-sale-price-label" data-default-translation="{{lang "page_builder.pdp-non-sale-price-label"}}">
                {{theme_settings.pdp-non-sale-price-label}}
            </span>
            <span data-product-non-sale-price-without-tax class="price price--non-sale">
                {{price.non_sale_price_without_tax.formatted}}
            </span>
        </div>
        <div class="price-section price-section--withoutTax" {{#if schema_org}}itemprop="offers" itemscope itemtype="https://schema.org/Offer"{{/if}}>
            <span class="price-label" {{#if price.non_sale_price_without_tax}}style="display: none;"{{/if}}>
                {{theme_settings.pdp-price-label}}
            </span>
            <span class="price-now-label"
                  {{#unless price.non_sale_price_without_tax}}style="display: none;"{{/unless}}
                  data-page-builder-key="pdp-sale-price-label"
                  data-default-translation="{{lang "page_builder.pdp-sale-price-label"}}"
            >
                {{theme_settings.pdp-sale-price-label}}
            </span>
            <span data-product-price-without-tax class="price price--withoutTax{{#if price.with_tax}} price-section--minor{{/if}}">{{price.without_tax.formatted}}</span>
            {{#if schema_org}}
                <meta itemprop="availability" itemtype="https://schema.org/ItemAvailability"
                    content="https://schema.org/{{#if product.pre_order}}PreOrder{{else if product.out_of_stock}}OutOfStock{{else if product.can_purchase '===' false}}OutOfStock{{else}}InStock{{/if}}">
                <meta itemprop="itemCondition" itemtype="https://schema.org/OfferItemCondition" content="https://schema.org/{{product.condition}}Condition">
                <meta itemprop="priceCurrency" content="{{currency_selector.active_currency_code}}">
                <meta itemprop="url" content="{{product.url}}">
                <div itemprop="priceSpecification" itemscope itemtype="https://schema.org/PriceSpecification">
                    <meta itemprop="price" content="{{price.without_tax.value}}">
                    <meta itemprop="priceCurrency" content="{{currency_selector.active_currency_code}}">
                    <meta itemprop="valueAddedTaxIncluded" content="false">
                </div>
            {{/if}}
            {{#if price.with_tax}}
                <abbr title="{{lang 'products.excluding_tax'}}">{{lang 'products.price_without_tax' tax_label=price.tax_label}}</abbr>
            {{/if}}
        </div>
    {{/if}}
    {{#if page_type '===' 'product'}}
         <div class="price-section price-section--saving price" {{#unless price.saved}}style="display: none;"{{/unless}}>
                <span class="price">{{lang 'products.you_save_opening_text'}}</span>
                <span data-product-price-saved class="price price--saving">
                    {{price.saved.formatted}}
                </span>
                <span class="price">{{lang 'products.you_save_closing_bracket'}}</span>
         </div>
    {{/if}}
{{/and}}

components/products/price-range:
{{#and price.retail_price_range.min.with_tax price.retail_price_range.max.with_tax}}
